import React, { useState } from "react";

export default function Inbox() {
  const [newTask, setNewTask] = useState(false);
  return (
    <div>
      <h2>Inbox</h2>
      {!newTask && (
        <button
          onClick={() => {
            setNewTask(true);
          }}
        >
          +AddNew
        </button>
      )}
      {newTask && (
        <form>
          <input type="text" />
          <button>Add Task</button>
          <button>Cancel</button>
          <input type="date" defaultValue={"2023-01-01"} />
        </form>
      )}
    </div>
  );
}
