import React, { useState } from "react";

export default function Inbox() {
  const [newTask, setNewTask] = useState(false);
  return (
    <div>
      <h2>Inbox</h2>
      {!newTask && <button>+AddNew</button>}
      {
        newTask && (
            <input type="text" />
        )
      }
    </div>
  );
}
